from django.conf import settings
from django.utils.translation import gettext_lazy as _

# The default grid size for Bootstrap 5 is 12. You can change this setting
# to whatever layout you require. We suggest that the value is at
# least devisable by 2, 3 and 4.
GRID_SIZE = getattr(
    settings,
    "DJANGOCMS_FRONTEND_GRID_SIZE",
    12,
)

# Bootstrap 5 provides more than 2 container types, .container and .container-fluid
# https://getbootstrap.com/docs/5.0/layout/grid/#no-gutters
GRID_CONTAINER_CHOICES = getattr(
    settings,
    "DJANGOCMS_FRONTEND_GRID_CONTAINERS",
    (
        ("container", _("Contenedor")),
        ("container-fluid", _("Contenedor fluido")),
        ("container-full", _("Contenedor completo")),
    ),
)

# Options for flexbox on the alignment of the grid
# https://flexbox.webflow.com/
GRID_ROW_VERTICAL_ALIGNMENT_CHOICES = (
    ("align-items-start", _("Alinear elementos al inicio")),
    ("align-items-center", _("Alinear elementos al centro")),
    ("align-items-end", _("Alinear elementos al final")),
)

GRID_ROW_HORIZONTAL_ALIGNMENT_CHOICES = (
    ("justify-content-start", _("Justificar al inicio")),
    ("justify-content-center", _("Justificar al centro")),
    ("justify-content-end", _("Justificar al final")),
    ("justify-content-around", _("Justificar alrededor")),
    ("justify-content-between", _("Justificar entre")),
)

GRID_COLUMN_ALIGNMENT_CHOICES = (
    ("align-self-start", _("Alinearse al inicio")),
    ("align-self-center", _("Alinearse al centro")),
    ("align-self-end", _("Alinearse al final")),
)
